mapper.select:
select t.person_name,t.person_name personName,t.bank_code,t.bank_code bankCode,t.bank_id,t.bank_id
bankId,t.create_time,t.create_time createTime,t.bank_name,t.bank_name bankName,t.status_cd,t.status_cd
statusCd,t.shop_id,t.shop_id shopId,t.person_tel,t.person_tel personTel
from account_bank t
where 1 =1

    and t.person_name= #{personName}


    and t.bank_code= #{bankCode}


    and t.bank_id= #{bankId}


    and t.create_time= #{createTime}


    and t.bank_name= #{bankName}


    and t.status_cd= #{statusCd}


    and t.shop_id= #{shopId}


    and t.person_tel= #{personTel}

order by t.create_time desc

    limit #{page}, #{row}

mapper.update:
update account_bank t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}


    , t.person_name= #{personName}


    , t.bank_code= #{bankCode}


    , t.create_time= #{createTime}


    , t.bank_name= #{bankName}


    , t.shop_id= #{shopId}


    , t.person_tel= #{personTel}

where 1=1

    and t.bank_id= #{bankId}

mapper.select:
