mapper.select:
SELECT a.`p_id` pId, a.`privilege_flag` privilegeFlag, a.`user_id` userId
FROM p_privilege_user a
WHERE a.`p_id` = #{pId}
  AND a.`user_id` = #{userId}
  AND a.`privilege_flag` = '1'
  AND a.`status_cd` = '0'

mapper.update:
UPDATE p_privilege_user a
SET a.`status_cd` = '1'
WHERE a.`user_id` = #{userId}

UPDATE p_privilege_group a SET

    a.name = #{name},


    a.description = #{description}

WHERE 1= 1

    and a.store_id = #{storeId}

and a.pg_id = #{pgId}

    and a.domain= #{storeTypeCd}

UPDATE p_privilege_group a SET a.`status_cd` = '1'
WHERE 1= 1

    and a.store_id = #{storeId}

and a.pg_id = #{pgId}

    and a.domain= #{storeTypeCd}

UPDATE p_privilege_rel a SET a.`status_cd` = '1'
WHERE 1=1

    and a.`pg_id` = #{pgId}


    and a.`p_id` = #{pId}

mapper.select:
select ppg.pg_id pgId,ppg.name,ppg.description,ppg.create_time createTime,ppg.store_id storeId,ppg.domain
from p_privilege_group ppg
where 1=1

    and ppg.pg_id = #{pgId}


    and ppg.store_id = #{storeId}


    and ppg.domain = #{storeTypeCd}

and ppg.status_cd = '0'

SELECT ppu.`pu_id` puId, ppu.`p_id` pId, ppu.`privilege_flag` privilegeFlag, ppu.`user_id` userId FROM p_privilege_user ppu WHERE 1 = 1
AND ppu.`privilege_flag` = #{privilegeFlag}
AND ppu.`p_id` = #{pId}
AND ppu.`user_id` = #{userId}
AND ppu.`status_cd` = '0'

mapper.update:
update p_privilege_user ppu set ppu.status_cd = '1'
where 1=1

    and ppu.p_id= #{pId}


    and ppu.privilege_flag = #{privilegeFlag}


    and ppu.user_id = #{userId}

and ppu.status_cd = '0'

mapper.select:
select distinct t.user_id userId from p_privilege_user t INNER JOIN p_privilege p on t.p_id = p.p_id and p.status_cd = '0' where t.privilege_flag = '0' and t.status_cd = '0'
and p.resource = #{resource}
and t.store_id = #{storeId}
union ALL select distinct t.user_id userId from p_privilege_user t inner join p_privilege_group pg on t.p_id = pg.pg_id and pg.status_cd = '0' and pg.pg_id != '600201904002' inner join p_privilege_rel pr on pg.pg_id = pr.pg_id and pr.status_cd ='0' INNER JOIN p_privilege p on pr.p_id = p.p_id and p.status_cd = '0'
LEFT JOIN u_role_community rc on pg.pg_id = rc.role_id and rc.status_cd = '0'
where t.status_cd = '0' and t.privilege_flag = '1'
and p.resource = #{resource}
and t.store_id = #{storeId}
