mapper.select:
select
ms.community_member_id,ms.b_id,ms.community_id,ms.member_id,ms.member_type_cd,ms.operate,ms.audit_status_cd,ms.start_time,ms.end_time
from business_community_member ms where 1 = 1

    and ms.operate = #{operate}


    and ms.b_id = #{bId}


    and ms.community_id = #{communityId}

select ms.community_member_id , ms.b_id,ms.community_id,ms.member_id,ms.member_type_cd,ms.status_cd,ms.audit_status_cd, ms.community_member_id communityMemberId ,ms.b_id bId,ms.community_id communityId,ms.member_id memberId,ms.member_type_cd memberTypeCd,ms.status_cd statusCd, ms.audit_status_cd auditStatusCd, ms.start_time,ms.end_time,ms.start_time startTime,ms.end_time endTime, ms.create_time createTime, (CASE WHEN ms.audit_status_cd='1000' THEN '待审核' WHEN ms.audit_status_cd = '1100' THEN '审核完成' ELSE '审核拒绝' END) stateName
,sc.name
from s_community_member ms
,s_community sc
where 1=1
and sc.`community_id` = ms.`community_id` AND sc.`status_cd` = '0' and sc.state='1100'
and ms.status_cd = #{statusCd}
and ms.b_id = #{bId}
and ms.community_member_id = #{communityMemberId}
AND ms.`community_id` = msc.`community_id` and msc.member_id = #{memberId} and msc.status_cd = '0'
and ms.member_id = #{memberId}
and ms.member_id = #{subMemberId}
and ms.member_type_cd not in ('390001200003','390001200005','390001200000')
and ms.member_type_cd = #{memberTypeCd}
and ms.audit_status_cd = #{auditStatusCd}
and ms.community_id = #{communityId}
and sc.name like concat('%',#{communityName},'%')
order by ms.create_time desc
limit #{page},#{row}

mapper.update:
update s_community_member ms set ms.status_cd = #{statusCd}

    ,ms.b_id = #{newBId}


    ,ms.audit_status_cd = #{auditStatusCd}


    ,ms.start_time = #{startTime}


    ,ms.end_time = #{endTime}

where 1=1

    and ms.b_id = #{bId}


    and ms.community_member_id = #{communityMemberId}

mapper.select:
select ms.community_id,count(1) count
from s_community_member ms

    ,s_community sc

where 1=1

    and sc.`community_id` = ms.`community_id`
    AND sc.`status_cd` = '0'
    and sc.state='1100'


    and ms.status_cd = #{statusCd}


    and ms.b_id = #{bId}


    and ms.community_member_id = #{communityMemberId}


    AND ms.`community_id` = msc.`community_id`
    and msc.member_id = #{memberId}
    and msc.status_cd = '0'


    and ms.member_id = #{memberId}


    and ms.member_id = #{subMemberId}


    and ms.member_type_cd not in ('390001200003','390001200005','390001200000')


    and ms.member_type_cd = #{memberTypeCd}


    and ms.audit_status_cd = #{auditStatusCd}


    and ms.audit_status_cd in

        #{item}



    and ms.community_id = #{communityId}


    and sc.name like concat('%',#{communityName},'%')

select t.address,t.nearby_landmarks,t.nearby_landmarks nearbyLandmarks,
t.city_code,t.city_code cityCode,t.name,t.status_cd,t.status_cd statusCd,t.tel,
t.community_id,t.community_id communityId,t.b_id,t.b_id bId,t.map_y,t.map_y mapY,
t.map_x,t.map_x mapX,t.state,(CASE
WHEN t.state='1000'
THEN '待审核'
WHEN t.state = '1100'
THEN '审核完成'
ELSE
'审核拒绝'
END) stateName,ca.area_code areaCode,ca.area_name areaName,
ca.parent_area_code parentAreaCode,ca.parent_area_name parentAreaName,
t.community_area communityArea,t.pay_fee_month,t.pay_fee_month payFeeMonth,t.fee_price,t.fee_price
feePrice,t.qr_code qrCode
from s_community t,city_area ca

    ,s_community_member cm

where t.status_cd = '0'
and t.city_code = ca.area_code
and ca.status_cd = '0'

    and t.address= #{address}


    and t.nearby_landmarks= #{nearbyLandmarks}


    and t.city_code= #{cityCode}


    and t.name like concat('%',#{name},'%')



    and t.status_cd= #{statusCd}


    and t.community_id= #{communityId}


    and t.community_id in

        #{item}



    and t.b_id= #{bId}


    and t.map_y= #{mapY}


    and t.map_x= #{mapX}


    and t.state= #{state}


    and cm.community_id = t.community_id
    and cm.member_id = #{memberId}
    and cm.status_cd = '0'


    and cm.audit_status_cd = #{auditStatusCd}


    and t.community_id not in

        #{item}



order by t.create_time desc

    limit #{page}, #{row}

select count(DISTINCT(t.community_id)) count
from s_community t,city_area ca

    ,s_community_member cm

where 1 =1
and t.city_code = ca.area_code
and ca.status_cd = '0'

    and t.address= #{address}


    and t.nearby_landmarks= #{nearbyLandmarks}


    and t.city_code= #{cityCode}


    and t.name like concat('%',#{name},'%')


    and t.status_cd= #{statusCd}


    and t.community_id= #{communityId}


    and t.community_id in

        #{item}



    and t.b_id= #{bId}


    and t.map_y= #{mapY}


    and t.map_x= #{mapX}


    and t.state= #{state}


    and cm.community_id = t.community_id
    and cm.member_id = #{memberId}
    and cm.status_cd = '0'


    and cm.audit_status_cd = #{auditStatusCd}


    and t.community_id not in

        #{item}

SELECT sc.`community_id` communityId, sc.`name`, sc.`address`, sc.`nearby_landmarks` nearbyLandmarks, sc.`city_code` cityCode, sc.`map_x` mapX, sc.`map_y` mapY, sc.tel, scm.`community_member_id` communityMemberId, scm.`member_id` memberId, scm.`member_type_cd` memberTypeCd, scm.audit_status_cd auditStatusCd, scm.`status_cd` statusCd, scm.`start_time` startTime, scm.`end_time` endTime, ca.area_name areaName, ca.parent_area_name cityName, ca1.parent_area_name provName, sc.community_area communityArea, sc.qr_code qrCode FROM s_community sc, s_community_member scm, city_area ca, city_area ca1 WHERE sc.`community_id` = scm.`community_id` AND sc.`status_cd` = '0' and sc.city_code = ca.area_code and ca.parent_area_code = ca1.area_code AND scm.`member_id` = #{memberId} AND scm.`member_type_cd` = #{memberTypeCd} AND scm.`status_cd` = '0'
and scm.audit_status_cd in ('1000', '1100','1200')
and scm.audit_status_cd = #{auditStatusCd}
and sc.community_id = #{communityId}
