mapper.select:
select count(1) count
from inspection_task t,inspection_plan ip,t_dict td1,t_dict td2
where 1 =1
and t.sign_type = td1.status_cd
and td1.table_name = 'inspection_task'
and td1.table_columns = 'sign_type'
and t.state = td2.status_cd
and td2.table_name = 'inspection_task'
and td2.table_columns = 'state'
and ip.inspection_plan_id = t.inspection_plan_id
and ip.status_cd = '0'

    and t.plan_user_id= #{planUserId}


    and t.original_plan_user_id= #{originalPlanUserId}


    and t.act_ins_time >= #{startTime}


    and t.act_ins_time <= #{endTime}


    and DATE_FORMAT(t.plan_ins_time,'%Y-%m-%d')= #{planInsTime}


    and t.plan_end_time= #{planEndTime}


    and DateDiff(plan_ins_time,NOW())=0


    and t.status_cd= #{statusCd}


    and t.act_user_name= #{actUserName}


    and t.sign_type= #{signType}


    and t.inspection_plan_id= #{inspectionPlanId}


    and ip.inspection_plan_name = #{inspectionPlanName}


    and t.plan_user_name like '%${planUserName}%'


    and t.community_id= #{communityId}


    and t.b_id= #{bId}


    and t.act_user_id= #{actUserId}


    and t.task_id= #{taskId}


    and t.state= #{state}


    and t.state in

        #{item}

SELECT ip.inspection_plan_id inspectionPlanId, ip.inspection_plan_name inspectionPlanName, ip.inspection_plan_period inspectionPlanPeriod, ip.sign_type signType, ip.community_id communityId, ip.end_time endTime, ip.staff_id planUserId, ip.staff_name planUserName FROM inspection_plan ip inner join LEFT JOIN inspection_task it ON ip.inspection_plan_id = it.inspection_plan_id AND ip.staff_id = it.plan_user_id AND it.status_cd = '0' AND ip.community_id = it.community_id
and it.plan_ins_time > DATE_SUB(curdate(),INTERVAL 7 DAY)
and it.plan_ins_time > DATE_SUB(curdate(),INTERVAL 30 DAY)
WHERE ip.status_cd = '0' and ip.end_time > now() and ip.start_time < now() and ip.state = '2020025' AND it.inspection_plan_id IS NOT NULL and ip.community_id = #{communityId}
and ip.inspection_plan_period= #{inspectionPlanPeriod}
limit 1000
