mapper.select:
select t.g_id,t.name,t.icon,t.label,t.seq,t.description,t.g_id gId
from m_menu_group t
where t.status_cd= '0'

    and t.g_id= #{gId}


    and t.name like concat('%',#{name},'%')


    and t.icon= #{icon}


    and t.label= #{label}


    and t.seq= #{seq}


    and t.description= #{description}

order by t.create_time desc

    limit #{page}, #{row}

select count(1) count
from m_menu_group t
where 1 =1

    and t.g_id= #{gId}


    and t.name like concat('%',#{name},'%')


    and t.icon= #{icon}


    and t.status_cd= #{statusCd}


    and t.label= #{label}


    and t.seq= #{seq}


    and t.description= #{description}

select t.p_id,t.name,t.domain,t.resource,t.description,t.p_id pId,t.create_time createTime
from p_privilege t
where t.status_cd= '0'

    and t.p_id= #{pId}


    and t.name like concat('%',#{name},'%')


    and t.resource = #{resource}


    and t.domain= #{domain}


    and t.m_id= #{mId}

order by t.create_time desc

    limit #{page}, #{row}

select count(1) count
from p_privilege t
where 1 =1

    and t.p_id= #{pId}


    and t.name like concat('%',#{name},'%')


    and t.resource = #{resource}


    and t.domain= #{domain}


    and t.m_id= #{mId}


    and t.status_cd= #{statusCd}

select t.m_id mId,t.name,t.g_id gId,t.url,t.seq,t.p_id pId,t.description,t.is_show isShow, if(t.is_show='Y','显示','不显示') isShowName, p.p_id pId,p.name pName from m_menu t,p_privilege p where t.status_cd= '0' and p.status_cd= '0' and t.m_id = p.m_id
and t.g_id= #{gId}
and t.name like concat('%',#{name},'%')
and t.name like concat('%',#{mName},'%')
and p.domain= #{domain}
and p.name like concat('%',#{pName},'%')
and t.m_id= #{mId}
and p.p_id= #{pId}
and t.is_show= #{isShow}
and t.url like concat('%',#{url},'%')
and t.seq= #{seq}
and t.description= #{description}
and exists( SELECT 1 FROM p_privilege_user ppu,u_user u WHERE ppu.`p_id` = p.`p_id` AND ppu.`privilege_flag` = '0' and ppu.user_id = u.user_id AND u.`name` = #{uName} AND ppu.`status_cd` = '0' UNION SELECT 1 FROM p_privilege_user ppu, p_privilege_group ppg,p_privilege_rel ppr,u_user u WHERE ppu.`p_id` = ppr.pg_id AND ppr.pg_id = ppg.pg_id AND ppr.p_id = p.`p_id` AND ppu.`privilege_flag` = '1' and ppu.user_id = u.user_id AND u.`name` = #{uName} AND ppu.`status_cd` = '0' AND ppg.status_cd = '0' AND ppr.status_cd = '0' )
order by t.seq asc
limit #{page}, #{row}

select count(1) count from m_menu t,p_privilege p where t.status_cd= '0' and p.status_cd= '0' and t.m_id = p.m_id
and t.g_id= #{gId}
and t.name like concat('%',#{name},'%')
and t.name like concat('%',#{mName},'%')
and t.p_id= #{pId}
and p.domain= #{domain}
and p.name like concat('%',#{pName},'%')
and p.m_id= #{mId}
and t.p_id= #{pId}
and t.is_show= #{isShow}
and t.url like concat('%',#{url},'%')
and t.seq= #{seq}
and t.description= #{description}
and exists( SELECT 1 FROM p_privilege_user ppu,u_user u WHERE ppu.`p_id` = p.`p_id` AND ppu.`privilege_flag` = '0' and ppu.user_id = u.user_id AND u.`name` = #{uName} AND ppu.`status_cd` = '0' UNION SELECT 1 FROM p_privilege_user ppu, p_privilege_group ppg,p_privilege_rel ppr,u_user u WHERE ppu.`p_id` = ppr.pg_id AND ppr.pg_id = ppg.pg_id AND ppr.p_id = p.`p_id` AND ppu.`privilege_flag` = '1' and ppu.user_id = u.user_id AND u.`name` = #{uName} AND ppu.`status_cd` = '0' AND ppg.status_cd = '0' AND ppr.status_cd = '0' )
