mapper.select:
select t.area,t.operate,t.num,t.ps_id,t.ps_id
psId,t.pa_id,t.pa_id
paId,t.remark,t.state,t.community_id,t.community_id communityId,t.b_id,t.b_id bId,t.parking_type,t.parking_type
parkingType
from business_parking_space t
where 1 =1

    and t.area= #{area}


    and t.operate= #{operate}


    and t.num= #{num}


    and t.ps_id= #{psId}


    and t.pa_id= #{paId}


    and t.remark= #{remark}


    and t.state= #{state}


    and t.community_id= #{communityId}


    and t.b_id= #{bId}


    and t.parking_type= #{parkingType}

mapper.insert:
insert into parking_space(
area,num,ps_id,pa_id,remark,status_cd,state,community_id,b_id,parking_type
) select t.area,t.num,t.ps_id,t.pa_id,t.remark,'0',t.state,t.community_id,t.b_id,t.parking_type
from
business_parking_space t where 1=1

    and t.area= #{area}

and t.operate= 'ADD'

    and t.num= #{num}


    and t.ps_id= #{psId}


    and t.pa_id= #{paId}


    and t.remark= #{remark}


    and t.state= #{state}


    and t.community_id= #{communityId}


    and t.b_id= #{bId}


    and t.parking_type= #{parkingType}

mapper.select:
SELECT t.area, t.num, t.ps_id psId, t.ps_id, t.pa_id paId, t.pa_id, t.remark, t.status_cd statusCd, t.status_cd, t.`state`, t.community_id, t.community_id communityId, t.b_id, t.b_id bId, t.parking_type, t.parking_type parkingType, td2.NAME parkingTypeName, td1.NAME stateName, pa.num areaNum, pa.type_cd typeCd, t.create_time createTime FROM parking_space t LEFT JOIN t_dict td1 ON t.state = td1.status_cd AND td1.table_name = 'parking_space' AND td1.table_columns = 'state' LEFT JOIN t_dict td2 ON t.parking_type = td2.status_cd AND td2.table_name = 'parking_space' AND td2.table_columns = 'parking_type' LEFT JOIN parking_area pa ON t.pa_id = pa.pa_id AND pa.status_cd = '0'
and t.area= #{area}
and t.num= #{num}
and pa.num= #{areaNum}
and t.ps_id= #{psId}
and t.pa_id= #{paId}
and t.remark= #{remark}
and t.parking_type= #{parkingType}
and t.status_cd= #{statusCd}
and t.state= #{state}
and t.state in
#{item}
and t.ps_id in
#{item}
and t.pa_id in
#{item}
and t.community_id= #{communityId}
and t.b_id= #{bId}
order by t.create_time desc
limit #{page}, #{row}

mapper.update:
update parking_space t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}


