mapper.select:
select t.area,t.operate,t.type_cd,t.type_cd typeCd,t.num,t.ps_id,t.ps_id
psId,t.remark,t.state,t.community_id,t.community_id communityId,t.b_id,t.b_id bId,t.user_id,t.user_id userId
from business_parking_space t
where 1 =1

    and t.area= #{area}


    and t.operate= #{operate}


    and t.type_cd= #{typeCd}


    and t.num= #{num}


    and t.ps_id= #{psId}


    and t.remark= #{remark}


    and t.state= #{state}



    and t.community_id= #{communityId}


    and t.b_id= #{bId}


    and t.user_id= #{userId}

mapper.insert:
insert into p_parking_space(
area,type_cd,num,ps_id,remark,status_cd,state,community_id,b_id,user_id
) select t.area,t.type_cd,t.num,t.ps_id,t.remark,'0',t.state,t.community_id,t.b_id,t.user_id from
business_parking_space t where 1=1

    and t.area= #{area}

and t.operate= 'ADD'

    and t.type_cd= #{typeCd}


    and t.num= #{num}


    and t.ps_id= #{psId}


    and t.remark= #{remark}


    and t.state= #{state}


    and t.community_id= #{communityId}


    and t.b_id= #{bId}


    and t.user_id= #{userId}

mapper.select:
SELECT
    t.area,
    t.`type_cd` typeCd,
    t.`type_cd`,
    t.num,
    t.ps_id psId,
    t.ps_id,
    t.remark,
    t.status_cd statusCd,
    t.status_cd,
    t.`state`,
    t.community_id,
    t.community_id communityId,
    t.b_id,
    t.b_id bId,
    t.user_id,
    t.user_id userId,
    td.name stateName
FROM
  p_parking_space t,t_dict td

    t.state = td.status_cd
    and td.table_name='p_parking_space'
    and td.table_columns='state'

        and t.area= #{area}


        and t.type_cd= #{typeCd}


        and t.num= #{num}


        and t.ps_id= #{psId}


        and t.remark= #{remark}


        and t.status_cd= #{statusCd}


        and t.state= #{state}


        and t.state in

            #{item}



        and t.ps_id in

            #{item}



        and t.community_id= #{communityId}


        and t.b_id= #{bId}


        and t.user_id= #{userId}



        limit #{page}, #{row}

mapper.update:
update p_parking_space t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}


