communityId
from business_building_room t
where 1 =1

    and t.fee_coefficient= #{feeCoefficient}


    and t.section= #{section}


    and t.remark= #{remark}


    and t.user_id= #{userId}


    and t.room_id= #{roomId}


    and t.layer= #{layer}


