mapper.select:
select t.amount,t.obj_id,t.obj_id objId,t.remark,t.status_cd,t.status_cd statusCd,t.obj_name,t.obj_name
objName,t.community_id,t.community_id communityId,t.receipt_id,t.receipt_id receiptId,t.obj_type,t.obj_type
objType,t.create_time createTime,frd.fee_name feeName,t.pay_obj_id payObjId,t.pay_obj_name payObjName,pf.fee_type_cd feeTypeCd,
frd.start_time startTime,frd.end_time endTime,pf.fee_flag feeFlag
from fee_receipt t
inner join fee_receipt_detail frd on t.receipt_id = frd.receipt_id and frd.status_cd = '0'
inner join pay_fee pf on frd.fee_id = pf.fee_id and pf.status_cd = '0'
where 1 =1

    and t.amount= #{amount}


    and t.obj_id= #{objId}


    and t.pay_obj_id= #{payObjId}


    and t.pay_obj_name= #{payObjName}


    and t.remark= #{remark}


    and t.status_cd= #{statusCd}


    and t.obj_name= #{objName}


    and t.community_id= #{communityId}


    and t.receipt_id= #{receiptId}


    and t.receipt_id in

        #{item}



    and frd.detail_id in

        #{item}



    and frd.fee_id = #{feeId}


    and t.obj_type= #{objType}


    and t.create_time >= #{qstartTime}


    and t.create_time <= #{qendTime}

order by t.create_time desc

    limit #{page}, #{row}

SELECT * FROM ( SELECT t.obj_name AS objName, '' AS carNum, bo.NAME AS name, b.built_up_area AS roomArea, tt.fee_name feeName, tt.area num, SPLIT_STR ( tt.square_price, '/', 1 ) AS unit, CASE pf.fee_flag WHEN '1003006' THEN tt.start_time ELSE NULL END start, tt.start_time as startTime, CASE pf.fee_flag WHEN '1003006' THEN tt.end_time ELSE NULL END end, tt.end_time as endTime, tt.cycle as cycle, tt.amount amount, uu.NAME AS userName, t.create_time AS createTime, p.remark as remark FROM fee_receipt t LEFT JOIN fee_receipt_detail tt ON t.receipt_id = tt.receipt_id LEFT JOIN building_room b ON t.obj_id = b.room_id LEFT JOIN building_owner_room_rel borr ON b.room_id = borr.room_id LEFT JOIN building_owner bo ON borr.owner_id = bo.owner_id LEFT JOIN pay_fee pf ON tt.fee_id = pf.fee_id LEFT JOIN pay_fee_detail p ON tt.fee_id = p.fee_id AND tt.detail_id = p.detail_id LEFT JOIN c_business cb ON p.b_id = cb.b_id LEFT JOIN c_orders co ON cb.o_id = co.o_id LEFT JOIN u_user uu ON co.user_id = uu.user_id WHERE t.status_cd = '0' AND t.obj_type = '3333' AND p.state='1400' AND tt.status_cd = '0' AND b.status_cd = '0' AND borr.status_cd = '0' AND bo.status_cd = '0' AND bo.owner_type_cd = '1001' AND p.status_cd = '0'
and t.community_id= #{communityId}
UNION ALL SELECT CONCAT( f.floor_num, '栋', bu.unit_num, '单元', b.room_num, '室' ) AS objName, oc.car_num AS carNum, bo.NAME AS name, b.built_up_area AS roomArea, tt.fee_name feeName, tt.cycle num, SPLIT_STR ( tt.square_price, '/', 1 ) AS unit, CASE pf.fee_flag WHEN '1003006' THEN tt.start_time ELSE NULL END start, tt.start_time as startTime, CASE pf.fee_flag WHEN '1003006' THEN tt.end_time ELSE NULL END end, tt.end_time as endTime, tt.cycle as cycle, tt.amount amount, uu.NAME AS userName, t.create_time AS createTime, p.remark as remark FROM fee_receipt t LEFT JOIN fee_receipt_detail tt ON t.receipt_id = tt.receipt_id LEFT JOIN owner_car oc ON oc.car_id = t.obj_id and oc.car_type_cd='1001' LEFT JOIN building_owner bo ON bo.member_id = oc.owner_id LEFT JOIN building_owner_room_rel borr ON bo.owner_id = borr.owner_id LEFT JOIN building_room b ON borr.room_id = b.room_id LEFT JOIN building_room_attr ba ON b.room_id = ba.room_id LEFT JOIN building_unit bu ON b.unit_id = bu.unit_id AND bu.status_cd = '0' LEFT JOIN f_floor f ON bu.floor_id = f.floor_id LEFT JOIN pay_fee pf ON tt.fee_id = pf.fee_id AND pf.status_cd = '0' LEFT JOIN pay_fee_detail p ON tt.fee_id = p.fee_id AND p.status_cd = '0' AND tt.detail_id = p.detail_id LEFT JOIN c_business cb ON p.b_id = cb.b_id LEFT JOIN c_orders co ON cb.o_id = co.o_id LEFT JOIN u_user uu ON co.user_id = uu.user_id WHERE t.obj_type = '6666' AND p.state='1400'
and t.status_cd= #{statusCd}
and t.community_id= #{communityId}
AND bo.owner_type_cd = '1001' ) AS t WHERE 1=1
AND feeName in ('预交物业管理费','装修垃圾清运费','装修电梯使用费','水费','电费')
AND feeName in ('装修保证金','施工进入证')
AND feeName in ('预交物业管理费','水费','电费','地下停车费','地上停车费')
AND feeName in ('地上停车费','地下停车费')
AND feeName in ('公共区域公摊电费','生活水泵公摊电费','走廊楼道应急公摊电费','电梯公摊电费')
AND DATE_FORMAT(t.createTime, '%Y-%m-%d') >= DATE_FORMAT( #{qstartTime}, '%Y-%m-%d' )
AND DATE_FORMAT(t.createTime, '%Y-%m-%d') <= DATE_FORMAT( #{qendTime}, '%Y-%m-%d' )
and t.amount= #{amount}
and t.obj_id= #{objId}
and t.remark= #{remark}
and t.objName= #{objName}
and t.receipt_id= #{receiptId}
and t.obj_type= #{objType}
order by t.createTime desc
limit #{page}, #{row}

mapper.update:
update fee_receipt t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}


    , t.amount= #{amount}


    , t.obj_id= #{objId}


    , t.remark= #{remark}


    , t.obj_name= #{objName}


    , t.community_id= #{communityId}


    , t.obj_type= #{objType}

where 1=1

    and t.receipt_id= #{receiptId}

