t.fee_end_time feeEndTime,
t.owner_id ownerId,
t.owner_name ownerName,
t.owner_tel ownerTel,
t.payer_obj_name payerObjName,
t.payer_obj_type payerObjType,
t.community_id communityId,
t.state,
t.create_time createTime
from bill_owe_fee t
where 1 =1

    and t.owe_id= #{oweId}


    and t.bill_id= #{billId}


    and t.fee_id= #{feeId}


