mapper.select:
select t.prime_rate,t.prime_rate primeRate,t.detail_id,t.detail_id
detailId,t.receivable_amount,t.receivable_amount receivableAmount,t.cycles,t.remark,t.status_cd,t.status_cd
statusCd,t.start_time,t.start_time startTime,t.received_amount,t.received_amount
receivedAmount,t.state,t.end_time,t.end_time endTime,t.community_id,t.community_id communityId,t.fee_id,t.fee_id
feeId,t.payable_amount,t.payable_amount payableAmount,t.pay_order_id payOrderId,t.cashier_id
cashierId,t.cashier_name cashierName,t.open_invoice openInvoice
from pay_fee_detail t
where 1 =1

    and t.prime_rate= #{primeRate}


    and t.detail_id= #{detailId}


    and t.receivable_amount= #{receivableAmount}


    and t.cycles= #{cycles}


    and t.remark= #{remark}


    and t.status_cd= #{statusCd}


    and t.start_time= #{startTime}


    and t.received_amount= #{receivedAmount}


    and t.payable_amount= #{payableAmount}


    and t.state= #{state}


    and t.end_time= #{endTime}


    and t.community_id= #{communityId}


    and t.fee_id= #{feeId}


    and t.pay_order_id= #{payOrderId}


    and t.cashier_id= #{cashierId}


    and t.cashier_name= #{cashierName}


    and t.open_invoice= #{openInvoice}

order by t.create_time desc

    limit #{page}, #{row}

mapper.update:
update pay_fee_detail t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}


    , t.prime_rate= #{primeRate}


    , t.receivable_amount= #{receivableAmount}


    , t.cycles= #{cycles}


    , t.remark= #{remark}


    , t.start_time= #{startTime}


    , t.received_amount= #{receivedAmount}


    , t.payable_amount= #{payableAmount}


    , t.state= #{state}


    , t.end_time= #{endTime}


    , t.pay_order_id= #{payOrderId}


    , t.cashier_id= #{cashierId}


    , t.cashier_name= #{cashierName}


    , t.open_invoice= #{openInvoice}

where 1=1

    and t.detail_id= #{detailId}


    and t.community_id= #{communityId}


    and t.fee_id= #{feeId}

mapper.select:
select count(1) count
from pay_fee_detail t
where 1 =1

    and t.prime_rate= #{primeRate}


    and t.detail_id= #{detailId}


    and t.receivable_amount= #{receivableAmount}


    and t.cycles= #{cycles}


    and t.remark= #{remark}


    and t.status_cd= #{statusCd}


    and t.start_time= #{startTime}


    and t.received_amount= #{receivedAmount}


    and t.payable_amount= #{payableAmount}


    and t.state= #{state}


    and t.end_time= #{endTime}


    and t.community_id= #{communityId}


    and t.fee_id= #{feeId}


    and t.pay_order_id= #{payOrderId}


    and t.cashier_id= #{cashierId}


    and t.cashier_name= #{cashierName}


    and t.open_invoice= #{openInvoice}

select * from ( select * from ( SELECT td.`name` feeTypeCdName, pfc.fee_name feeName, oo.`name` payObjName, pfd.`detail_id` detailId, pfd.`cycles`, pfd.`receivable_amount` receivableAmount, pfd.`received_amount` receivedAmount, uu.`name` userName, pf.fee_id feeId, DATE_FORMAT(pfd.create_time, '%Y-%m-%d %H:%i:%s') createTime, td2.`name` primeRate FROM pay_fee_detail pfd left join pay_fee pf on pf.`fee_id` = pfd.`fee_id` AND pf.`status_cd` = '0' left join pay_fee_config pfc on pf.config_id = pfc.config_id and pfc.status_cd = '0' left join t_dict td on pfc.`fee_type_cd` = td.`status_cd` AND td.`table_name` = 'pay_fee_config' AND td.`table_columns` = 'fee_type_cd' left join t_dict td2 on pfd.`prime_rate` = td2.`status_cd` AND td2.`table_name` = 'pay_fee_detail' AND td2.`table_columns` = 'prime_rate' left join c_business cb on pfd.`b_id` = cb.`b_id` left join c_orders co on cb.`o_id` = co.`o_id` left join u_user uu on co.`user_id` = uu.`user_id` left join s_store ss on pf.`income_obj_id` = ss.`store_id` AND ss.`status_cd` = '0' left join building_owner_room_rel borr on borr.`room_id` = pf.`payer_obj_id` AND borr.`status_cd` = '0' left join building_owner oo on oo.`owner_id` = borr.`owner_id` and oo.owner_type_cd = '1001' AND oo.`status_cd` = '0' WHERE 1 = 1 AND pfd.`status_cd` = '0' and pf.payer_obj_type = '3333' and pfd.state not in (1100, 1300)
and ss.`store_id` = #{storeId}
AND pfd.`create_time` >= #{startTime}
AND pfd.`create_time` <= #{endTime}
AND pfd.`community_id` = #{communityId}
AND pf.config_id = #{configId}
AND pfd.prime_rate= #{primeRate}
AND uu.name= #{userName}
) tc union select * from ( SELECT td.`name` feeTypeCdName, pfc.fee_name feeName, oo.`name` payObjName, pfd.`detail_id` detailId, pfd.`cycles`, pfd.`receivable_amount` receivableAmount, pfd.`received_amount` receivedAmount, uu.`name` userName, pf.fee_id feeId, DATE_FORMAT(pfd.create_time, '%Y-%m-%d %H:%i:%s') createTime, td2.`name` primeRate FROM pay_fee_detail pfd left join pay_fee pf on pf.`fee_id` = pfd.`fee_id` AND pf.`status_cd` = '0' left join pay_fee_config pfc on pf.config_id = pfc.config_id and pfc.status_cd = '0' left join t_dict td on pfc.`fee_type_cd` = td.`status_cd` AND td.`table_name` = 'pay_fee_config' AND td.`table_columns` = 'fee_type_cd' left join t_dict td2 on pfd.`prime_rate` = td2.`status_cd` AND td2.`table_name` = 'pay_fee_detail' AND td2.`table_columns` = 'prime_rate' left join c_business cb on pfd.`b_id` = cb.`b_id` left join c_orders co on cb.`o_id` = co.`o_id` left join u_user uu on co.`user_id` = uu.`user_id` left join s_store ss on pf.`income_obj_id` = ss.`store_id` AND ss.`status_cd` = '0' left join owner_car oc on oc.car_id = pf.`payer_obj_id` AND oc.`status_cd` = '0' left join building_owner oo on oo.`owner_id` = oc.`owner_id` and oo.owner_type_cd = '1001' AND oo.`status_cd` = '0' WHERE 1 = 1 AND pfd.`status_cd` = '0' and pf.payer_obj_type = '6666' and pfd.state not in (1100, 1300)
and ss.`store_id` = #{storeId}
AND pfd.`create_time` >= #{startTime}
AND pfd.`create_time` <= #{endTime}
AND pfd.`community_id` = #{communityId}
AND pf.`config_id` = #{configId}
AND pfd.`prime_rate` = #{primeRate}
AND uu.name= #{userName}
) rc) s order by createTime desc limit #{page}, #{row}

