mapper.select:
select t.amount,t.income_obj_id,t.income_obj_id incomeObjId,t.fee_type_cd,t.fee_type_cd feeTypeCd,t.start_time,t.start_time startTime,t.status_cd,t.status_cd statusCd,t.end_time,t.end_time endTime,t.community_id,t.community_id communityId,t.b_id,t.b_id bId,t.fee_id,t.fee_id feeId,t.user_id,t.user_id userId,t.payer_obj_id,t.payer_obj_id payerObjId,pfc.square_price squarePrice,pfc.additional_amount additionalAmount,t.fee_flag,t.fee_flag feeFlag,t.state,t.config_id,t.config_id configId, pfc.fee_name feeName,td1.name feeTypeCdName,td2.name stateName,td3.name feeFlagName,pfc.computing_formula computingFormula,pfc.computing_formula_text computingFormulaText, pfc.bill_type billType,t.payer_obj_type,t.payer_obj_type payerObjType,pfc.is_default isDefault,pfc.start_time configStartTime,pfc.end_time configEndTime,pfc.payment_cd paymentCd,pfc.payment_cycle paymentCycle, pfa.value importFeeName,pfa1.`value` deadlineTime,pfa1.`value` maxEndTime, pfa2.`value` ownerName,t.batch_id,t.batch_id batchId, pfc.pay_online payOnline,pfc.scale,pfc.decimal_place decimalPlace,pfc.units,pfc.prepayment_period prepaymentPeriod, pfrb.bill_name billName,pfrb.bill_id billId,pfrb.cur_year_month curYearMonth,pfrb.remark,pfrb.rule_id ruleId,pfrb.month_cycle monthCycle from pay_fee t inner join pay_fee_rule_bill pfrb on t.fee_id = pfrb.fee_id and pfrb.status_cd = '0' INNER join pay_fee_config pfc on t.config_id = pfc.config_id and pfc.status_cd = '0' left join t_dict td1 on pfc.fee_type_cd = td1.status_cd and td1.table_name = 'pay_fee_config' and td1.table_columns = 'fee_type_cd' left join t_dict td2 on t.state = td2.status_cd and td2.table_name = 'pay_fee' and td2.table_columns = 'state' left join t_dict td3 on t.fee_flag = td3.status_cd and td3.table_name = 'pay_fee' and td3.table_columns = 'fee_flag' LEFT JOIN pay_fee_attrs pfa on t.fee_id = pfa.fee_id and pfa.spec_cd = '390002' and pfa.status_cd = '0' LEFT JOIN pay_fee_attrs pfa1 on t.fee_id = pfa1.fee_id and pfa1.spec_cd = '390010' and pfa1.status_cd = '0' LEFT JOIN pay_fee_attrs pfa2 on t.fee_id = pfa2.fee_id and pfa2.spec_cd = '390008' and pfa2.status_cd = '0' where 1 =1
and pfrb.bill_name= #{billName}
and pfrb.config_id= #{configId}
and pfrb.bill_id= #{billId}
and pfrb.cur_year_month= #{curYearMonth}
and pfrb.status_cd= #{statusCd}
and pfrb.remark= #{remark}
and pfrb.rule_id= #{ruleId}
and pfrb.community_id= #{communityId}
and pfrb.batch_id= #{batchId}
and pfrb.fee_id= #{feeId}
order by pfrb.cur_year_month
limit #{page}, #{row}

mapper.update:
update pay_fee_rule_bill t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}


    , t.bill_name= #{billName}


    , t.config_id= #{configId}


    , t.cur_year_month= #{curYearMonth}


    , t.remark= #{remark}


where 1=1

    and t.bill_id= #{billId}


    and t.community_id= #{communityId}


    and t.fee_id= #{feeId}


    and t.rule_id= #{ruleId}


    and t.batch_id= #{batchId}

mapper.select:
select count(1) count
from pay_fee_rule_bill t
where 1 =1

    and t.bill_name= #{billName}


    and t.config_id= #{configId}


    and t.bill_id= #{billId}


    and t.cur_year_month= #{curYearMonth}


    and t.status_cd= #{statusCd}


    and t.remark= #{remark}


    and t.rule_id= #{ruleId}


    and t.community_id= #{communityId}


    and t.batch_id= #{batchId}


    and t.fee_id= #{feeId}
