mapper.select:
select t.type_name,t.type_name typeName,t.remark,t.status_cd,t.status_cd statusCd,t.community_id,t.community_id
communityId,t.store_id,t.store_id storeId,t.sms_way smsWay,t.wt_id,t.wt_id wtId,t.create_time createTime
from work_type t
where 1 =1

    and t.type_name like '%${typeName}%'


    and t.remark= #{remark}


    and t.status_cd= #{statusCd}


    and t.community_id= #{communityId}


    and t.store_id= #{storeId}


    and t.sms_way= #{smsWay}


    and t.wt_id= #{wtId}

order by t.create_time desc

    limit #{page}, #{row}

mapper.update:
update work_type t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}


    , t.type_name= #{typeName}


    , t.remark= #{remark}


    , t.sms_way= #{smsWay}

where 1=1

    and t.wt_id= #{wtId}


    and t.community_id= #{communityId}


    and t.store_id= #{storeId}

mapper.select:
select count(1) count
from work_type t
where 1 =1

    and t.type_name like '%${typeName}%'


    and t.remark= #{remark}


    and t.status_cd= #{statusCd}


    and t.community_id= #{communityId}


    and t.store_id= #{storeId}


    and t.sms_way= #{smsWay}


    and t.wt_id= #{wtId}
