mapper.select:
     select  count(1) count
      FROM building_owner t

and t.sex= #{sex}


and t.name= #{name}


and t.link= #{link}


and t.status_cd= #{statusCd}


and t.remark= #{remark}


and t.owner_id= #{ownerId}


and t.b_id= #{bId}


and t.user_id= #{userId}


and t.age= #{age}


and t.member_id= #{memberId}


          and t.owner_type_cd= #{ownerTypeCd}

select  count(1) count
FROM building_owner t , s_community_member cm
WHERE t.`member_id` = cm.`member_id`
AND cm.`community_id` = #{communityId}
AND cm.`status_cd` = '0'
AND t.`status_cd` = '0'

    and t.sex= #{sex}


    and t.name= #{name}


    and t.link= #{link}


    and t.status_cd= #{statusCd}


    and t.remark= #{remark}


    and t.owner_id= #{ownerId}


    and t.b_id= #{bId}


    and t.user_id= #{userId}


    and t.age= #{age}


    and t.member_id= #{memberId}


    and t.owner_type_cd= #{ownerTypeCd}

select  t.sex,t.name,t.link,t.status_cd,t.status_cd statusCd,t.remark,t.owner_id,t.owner_id ownerId,t.b_id,t.b_id bId,
    t.user_id,t.user_id userId,t.age,t.member_id,t.member_id memberId,t.owner_type_cd,t.owner_type_cd ownerTypeCd
    FROM building_owner t , s_community_member cm
    WHERE t.`member_id` = cm.`member_id`
    AND cm.`community_id` = #{communityId}
    AND cm.`status_cd` = '0'
    AND t.`status_cd` = '0'

    and t.sex= #{sex}


    and t.name= #{name}


    and t.link= #{link}


    and t.status_cd= #{statusCd}


    and t.remark= #{remark}


    and t.owner_id= #{ownerId}


    and t.b_id= #{bId}


    and t.user_id= #{userId}


    and t.age= #{age}


    and t.member_id= #{memberId}


    and t.owner_id in

        #{item}



        and t.owner_type_cd= #{ownerTypeCd}


    limit #{page}, #{row}
