mapper.select:
select count(1) count
from building_room t
where t.status_cd = '0'

    and t.state= #{state}


    and t.community_id= #{communityId}

select t.apartment,t.built_up_area builtUpArea,t.community_id communityId,t.create_time createTime, t.fee_coefficient feeCoefficient,t.layer,t.room_id roomId,t.room_num roomNum,t.section, t.state,bu.unit_id unitId,bu.unit_num unitNum,ff.floor_id floorId,ff.floor_num floorNum, bo.age,bo.id_card idCard,bo.link,bo.member_id memberId,bo.`name`,bo.`name` ownerName,bo.owner_id ownerId,t.room_type roomType from building_room t INNER JOIN building_unit bu on t.unit_id = bu.unit_id and bu.status_cd = '0' inner join f_floor ff on bu.floor_id = ff.floor_id and ff.status_cd = '0' LEFT JOIN building_owner_room_rel borr on t.room_id = borr.room_id and borr.status_cd = '0' left join building_owner bo on borr.owner_id = bo.member_id and bo.status_cd = '0' where t.status_cd = '0'
and t.state= #{state}
and t.community_id= #{communityId}
limit #{page}, #{row}

select count(1) count
from owner_car t
where t.status_cd = '0'

    and t.state= #{state}


    and t.community_id= #{communityId}

select t.car_brand carBrand,t.car_color carColor,t.car_id carId,t.car_num carNum,t.car_type carType, t.community_id communityId,bo.`name` ownerName,bo.id_card idCard,bo.link,bo.owner_id ownerId,ps.ps_id psId, ps.num,pa.pa_id paId,pa.num areaNum,ps.state,t.start_time startTime,t.end_time endTime from owner_car t INNER JOIN building_owner bo on t.owner_id = bo.member_id and t.status_cd = '0' left join parking_space ps on t.ps_id = ps.ps_id and ps.status_cd = '0' left join parking_area pa on ps.pa_id = pa.pa_id and pa.status_cd = '0' where t.status_cd = '0'
and t.state= #{state}
and t.community_id= #{communityId}
limit #{page}, #{row}

select t.address,t.nearby_landmarks,t.nearby_landmarks nearbyLandmarks,
t.city_code,t.city_code cityCode,t.name,t.status_cd,t.status_cd statusCd,t.tel,
t.community_id,t.community_id communityId,t.b_id,t.b_id bId,t.map_y,t.map_y mapY,
t.map_x,t.map_x mapX,t.state,ca.area_code areaCode,ca.area_name areaName,
ca.parent_area_code parentAreaCode,ca.parent_area_name parentAreaName,
t.community_area communityArea
from s_community t,city_area ca
where t.status_cd = '0'
and t.city_code = ca.area_code
and ca.status_cd = '0'

    and t.address= #{address}


    and t.nearby_landmarks= #{nearbyLandmarks}


    and t.city_code= #{cityCode}


    and t.name like concat('%',#{name},'%')


    and t.status_cd= #{statusCd}


    and t.community_id= #{communityId}


    and t.community_id in

        #{item}



    and t.b_id= #{bId}


    and t.map_y= #{mapY}


    and t.map_x= #{mapX}


    and t.state= #{state}

order by t.create_time ASC

    limit #{page}, #{row}
