select f.floor_num floorNum, ( select ifnull(SUM(t.receivable_amount),0) from pay_fee_detail_month t inner join building_room br on t.obj_id = br.room_id and br.status_cd = '0' inner join building_unit bu on br.unit_id = bu.unit_id and bu.status_cd = '0' inner join pay_fee pf on t.fee_id = pf.fee_id and pf.status_cd = '0' where 1=1 and bu.floor_id = f.floor_id and t.community_id = f.community_id
and pf.fee_type_cd = #{feeTypeCd}
and t.detail_id = '-1' and t.cur_month_time <= #{endTime} ) oweAmount, ( select ifnull(SUM(t.receivable_amount),0) from pay_fee_detail_month t inner join building_room br on t.obj_id = br.room_id and br.status_cd = '0' inner join building_unit bu on br.unit_id = bu.unit_id and bu.status_cd = '0' inner join pay_fee pf on t.fee_id = pf.fee_id and pf.status_cd = '0' where 1=1 and bu.floor_id = f.floor_id and t.community_id = f.community_id
and pf.fee_type_cd = #{feeTypeCd}
and t.detail_id = '-1' and t.cur_month_time <= #{endTime} and t.cur_month_time >= #{startTime} ) curOweAmount, ( select ifnull(SUM(t.receivable_amount),0) from pay_fee_detail_month t inner join building_room br on t.obj_id = br.room_id and br.status_cd = '0' inner join building_unit bu on br.unit_id = bu.unit_id and bu.status_cd = '0' inner join pay_fee pf on t.fee_id = pf.fee_id and pf.status_cd = '0' where 1=1 and bu.floor_id = f.floor_id and t.community_id = f.community_id
and pf.fee_type_cd = #{feeTypeCd}
and t.detail_id = '-1' and t.cur_month_time < #{startTime} ) preOweAmount from f_floor f where 1=1 and f.community_id = #{communityId}
and f.floor_num = #{floorNum}
and f.status_cd = '0'
limit #{page}, #{row}

