select count(1) count from building_room br inner join building_unit bu on br.unit_id = bu.unit_id and bu.status_cd = '0' inner join f_floor f on bu.floor_id = f.floor_id and f.status_cd = '0' inner join report_owe_fee rof on br.room_id = rof.payer_obj_id inner join pay_fee pf on rof.fee_id = pf.fee_id and pf.status_cd = '0' where 1=1 and br.community_id = #{communityId} and br.status_cd = '0' and rof.amount_owed > 0
and pf.fee_type_cd = #{feeTypeCd}
and f.floor_id = #{floorId}
and f.floor_num = #{floorNum}

select f.floor_num floorNum, concat(f.floor_num,'-',bu.unit_num,'-',br.room_num) roomName, br.built_up_area builtUpArea, rof.fee_name feeName, rof.end_time endTime, rof.deadline_time deadlineTime, ( select ifnull(SUM(t.receivable_amount),0) from pay_fee_detail_month t where 1=1 and t.fee_id = rof.fee_id and t.community_id = f.community_id and t.detail_id = '-1' and t.cur_month_time <= #{startTime} ) oweAmount, ( select ifnull(SUM(t.receivable_amount),0) from pay_fee_detail_month t where 1=1 and t.fee_id = rof.fee_id and t.community_id = f.community_id and t.detail_id = '-1' and t.cur_month_time <= #{startTime} and t.cur_month_time >= #{endTime} ) curOweAmount, ( select ifnull(SUM(t.receivable_amount),0) from pay_fee_detail_month t where 1=1 and t.fee_id = rof.fee_id and t.community_id = f.community_id and t.detail_id = '-1' and t.cur_month_time <= #{startTime} ) preOweAmount from building_room br inner join building_unit bu on br.unit_id = bu.unit_id and bu.status_cd = '0' inner join f_floor f on bu.floor_id = f.floor_id and f.status_cd = '0' inner join report_owe_fee rof on br.room_id = rof.payer_obj_id inner join pay_fee pf on rof.fee_id = pf.fee_id and pf.status_cd = '0' where 1=1 and br.community_id = #{communityId} and br.status_cd = '0' and rof.amount_owed > 0
and pf.fee_type_cd = #{feeTypeCd}
and f.floor_id = #{floorId}
and f.floor_num = #{floorNum}
limit #{page}, #{row}

select ifnull(sum(t.received_amount),0) total
from pay_fee_detail_month t
where 1=1
and t.create_time > #{startTime}
and t.create_time < #{endTime}
and t.fee_id = #{feeId}

    and concat(t.detail_year,lpad(t.detail_month,2,0)) = #{yearMonth}


    and concat(t.detail_year,lpad(t.detail_month,2,0)) < #{yearMonth}


    and concat(t.detail_year,lpad(t.detail_month,2,0)) > #{yearMonth}


    and t.community_id= #{communityId}

