select count(1) count
from (
select a.floorNum,a.oweAmount,b.curOweAmount
from
(select t.floor_num floorNum,SUM(rfms.owe_amount) oweAmount
from f_floor t
left join building_unit bu on t.floor_id = bu.floor_id and bu.status_cd = '0'
left join building_room br on br.unit_id = bu.unit_id and br.status_cd = '0'
LEFT JOIN report_fee_month_statistics rfms on rfms.obj_id = br.room_id and rfms.obj_type = '3333' and
rfms.status_cd = '0'
where t.status_cd = '0'

    and t.community_id= #{communityId}


    and br.room_num= #{roomNum}


    and rfms.obj_name= #{objName}


    and rfms.fee_year= #{feeYear}


    and rfms.fee_month= #{feeMonth}

GROUP BY t.floor_num
) a,
(select t.floor_num floorNum,SUM(curT.owe_amount) curOweAmount
from f_floor t
left join building_unit bu on t.floor_id = bu.floor_id and bu.status_cd = '0'
left join building_room br on br.unit_id = bu.unit_id and br.status_cd = '0'
inner join report_fee_month_statistics curT on curT.obj_id = br.room_id and curT.obj_type = '3333' and
curT.status_cd = '0' and curT.community_id = t.community_id
where t.status_cd = '0'

    and t.community_id= #{communityId}


    and br.room_num= #{roomNum}


    and curT.fee_year= #{feeYear}


    and curT.fee_month= #{feeMonth}

GROUP BY t.floor_num
) b
where a.floorNum = b.floorNum

    and a.floor_id = #{floorId}

) t

select a.floorNum,a.oweAmount,b.curOweAmount
from
(select t.floor_num floorNum,SUM(rfms.owe_amount) oweAmount
from f_floor t
left join building_unit bu on t.floor_id = bu.floor_id and bu.status_cd = '0'
left join building_room br on br.unit_id = bu.unit_id and br.status_cd = '0'
LEFT JOIN report_fee_month_statistics rfms on rfms.obj_id = br.room_id and rfms.obj_type = '3333' and
rfms.status_cd = '0'
where t.status_cd = '0'

    and t.community_id= #{communityId}


    and br.room_num= #{roomNum}


    and rfms.obj_name= #{objName}


    and rfms.fee_year= #{feeYear}


    and rfms.fee_month= #{feeMonth}

GROUP BY t.floor_num
) a,
(select t.floor_num floorNum,SUM(curT.owe_amount) curOweAmount
from f_floor t
left join building_unit bu on t.floor_id = bu.floor_id and bu.status_cd = '0'
left join building_room br on br.unit_id = bu.unit_id and br.status_cd = '0'
inner join report_fee_month_statistics curT on curT.obj_id = br.room_id and curT.obj_type = '3333' and
curT.status_cd = '0' and curT.community_id = t.community_id
where t.status_cd = '0'

    and t.community_id= #{communityId}


    and br.room_num= #{roomNum}


    and curT.fee_year= #{feeYear}


    and curT.fee_month= #{feeMonth}

GROUP BY t.floor_num
) b
where a.floorNum = b.floorNum

    and a.floor_id = #{floorId}


    limit #{page}, #{row}

select count(1) count
from (
select t.floor_num
from f_floor t
where 1=1
and t.community_id= #{communityId}
GROUP BY t.floor_num
) tt

select count(1) count from ( select t.payer_obj_name payerObjName,br.built_up_area builtUpArea,pfc.square_price squarePrice, t.fee_id feeId,t.end_time startTime,t.deadline_time endTime,t.amount_owed oweAmount from report_owe_fee t left join building_room br on t.payer_obj_id = br.room_id and br.status_cd = '0' left join pay_fee_config pfc on t.config_id = pfc.config_id and pfc.status_cd = '0' where t.amount_owed > 0 and pfc.fee_type_cd = '888800010001'
and t.community_id= #{communityId}
) tt

select t.payer_obj_name payerObjName,br.built_up_area builtUpArea,pfc.square_price squarePrice, t.fee_id feeId,t.end_time startTime,t.deadline_time endTime,t.amount_owed oweAmount from report_owe_fee t left join building_room br on t.payer_obj_id = br.room_id and br.status_cd = '0' left join pay_fee_config pfc on t.config_id = pfc.config_id and pfc.status_cd = '0' where t.amount_owed > 0 and pfc.fee_type_cd = '888800010001'
and t.community_id= #{communityId}
limit #{page}, #{row}

select ifnull(sum(t.received_amount),0) total
from pay_fee_detail_month t
where 1=1
and t.create_time > #{startTime}
and t.create_time < #{endTime}
and t.fee_id = #{feeId}

    and concat(t.detail_year,lpad(t.detail_month,2,0)) = #{yearMonth}


    and concat(t.detail_year,lpad(t.detail_month,2,0)) < #{yearMonth}


    and concat(t.detail_year,lpad(t.detail_month,2,0)) > #{yearMonth}


    and t.community_id= #{communityId}

mapper.update:
delete from report_fee_month_statistics where fee_id in

    #{item}

and community_id = #{communityId}
