mapper.select:
select
    t.operate,t.apply_order_id,t.apply_order_id applyOrderId,
    t.b_id,t.b_id bId,t.res_id,t.res_id resId
from business_purchase_apply_detail t
where 1 =1

    and t.operate= #{operate}


    and t.apply_order_id= #{applyOrderId}


    and t.b_id= #{bId}


    and t.res_id= #{resId}

mapper.insert:
insert into purchase_apply_detail(
apply_order_id,status_cd,id,b_id,res_id
) select t.apply_order_id,'0',t.id,t.b_id,t.res_id from business_purchase_apply_detail t where 1=1
and t.operate= 'ADD'

    and t.apply_order_id= #{applyOrderId}


    and t.id= #{id}


    and t.b_id= #{bId}


    and t.res_id= #{resId}

mapper.select:
select
t.apply_order_id applyOrderId,
t.status_cd statusCd,
t.b_id bId,
t.res_id resId,
t.remark remark,
t.quantity quantity
from purchase_apply_detail t
where 1 =1

    and t.apply_order_id= #{applyOrderId}


    and t.status_cd= #{statusCd}


    and t.id= #{id}


    and t.b_id= #{bId}


    and t.res_id= #{resId}


    limit #{page}, #{row}

mapper.update:
update purchase_apply_detail t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}


    , t.apply_order_id= #{applyOrderId}


    , t.res_id= #{resId}

where 1=1

    and t.id= #{id}


    and t.b_id= #{bId}

mapper.select:
select count(1) count
from purchase_apply_detail t
where 1 =1

    and t.apply_order_id= #{applyOrderId}


    and t.status_cd= #{statusCd}


    and t.id= #{id}


    and t.b_id= #{bId}


    and t.res_id= #{resId}
