mapper.select:
select
t.operate,
t.res_order_type,
t.res_order_type resOrderType,
t.description,
t.apply_order_id,
t.apply_order_id applyOrderId,
t.state,
t.store_id,
t.store_id storeId,
t.user_name userName,
t.b_id bId,
t.b_id,
t.user_id,
t.user_id userId,
t.status_cd statusCd,
t.end_user_name,
t.end_user_tel,
t.end_user_name endUserName,
t.end_user_tel endUserTel,
t.community_id communityId
from business_purchase_apply t
where 1 =1

    and t.operate= #{operate}


    and t.res_order_type= #{resOrderType}


    and t.description= #{description}


    and t.apply_order_id= #{applyOrderId}


    and t.state= #{state}


    and t.store_id= #{storeId}


    and t.b_id= #{bId}


    and t.user_id= #{userId}


    and t.end_user_name= #{endUserName}


    and t.end_user_tel= #{endUserTel}


    and t.community_id= #{communityId}

select
t.apply_order_id applyOrderId,t.b_id bId,t.res_id resId,t.quantity,t.remark,t.operate,t.status_cd statusCd
from business_purchase_apply_detail t
where 1 =1

    and t.operate= #{operate}



    and t.description= #{description}


    and t.apply_order_id= #{applyOrderId}


    and t.b_id= #{bId}

mapper.insert:
insert into purchase_apply
(res_order_type,description,apply_order_id,status_cd,state,store_id,user_name,b_id,user_id,end_user_name,end_user_tel,community_id)
select
t.res_order_type,t.description,t.apply_order_id,'0',t.state,t.store_id,t.user_name,t.b_id,t.user_id,t.end_user_name,t.end_user_tel,
t.community_id
from business_purchase_apply t where 1=1
and t.operate= 'ADD'

    and t.res_order_type= #{resOrderType}


    and t.description= #{description}


    and t.apply_order_id= #{applyOrderId}


    and t.state= #{state}


    and t.store_id= #{storeId}


    and t.b_id= #{bId}


    and t.user_id= #{userId}


    and t.community_id= #{communityId}

mapper.select:
select
t.res_order_type resOrderType,t.description,
t.apply_order_id applyOrderId,t.status_cd statusCd,t.state,
t.store_id storeId,t.user_name userName,
t.b_id bId,t.user_id userId,t.create_time createTime,d.name stateName,
t.end_user_name,
t.end_user_tel,
t.end_user_name endUserName,
t.end_user_tel endUserTel,
t.description,
t.create_user_id createUserId,
t.create_user_name createUserName,
t.warehousing_way warehousingWay,
t.community_id communityId
from purchase_apply t
inner join t_dict d on t.state = d.status_cd and d.table_name = 'purchase_apply' and d.table_columns = 'state'
where 1 = 1

    and t.res_order_type= #{resOrderType}


    and t.description= #{description}


    and t.apply_order_id= #{applyOrderId}


    and t.apply_order_id in

        #{item}



    and t.user_name like concat('%',#{userName},'%')


    and t.status_cd= #{statusCd}


    and t.state= #{state}


    and t.store_id= #{storeId}


    and t.b_id= #{bId}


    and t.end_user_name= #{endUserName}


    and t.end_user_tel= #{endUserTel}


    and t.user_id= #{userId}


    and t.community_id= #{communityId}

order by t.create_time desc

    limit #{page}, #{row}

select
t.res_order_type resOrderType,t.description,
t.apply_order_id applyOrderId,t.status_cd statusCd,t.state,
t.store_id storeId,t.user_name userName,
t.b_id bId,t.user_id userId,t.create_time createTime,d.name stateName,
de.apply_order_id applyOrderId,de.res_id resId,de.quantity,de.remark,rs.res_name
resName,rs.price,rs.stock,rs.res_code resCode,rs.description,
t.end_user_name,
t.end_user_tel,
t.end_user_name endUserName,
t.end_user_tel endUserTel,
t.community_id communityId
from purchase_apply t
inner join t_dict d on t.state = d.status_cd and d.table_name = 'purchase_apply' and d.table_columns = 'state'
inner join purchase_apply_detail de on de.apply_order_id = t.apply_order_id
inner join resource_store rs on de.res_id = rs.res_id
where 1 =1

    and t.res_order_type= #{resOrderType}


    and t.apply_order_id= #{applyOrderId}


    and t.status_cd= #{statusCd}


    and t.state= #{state}


    and t.store_id= #{storeId}


    and t.b_id= #{bId}


    and t.end_user_name= #{endUserName}


    and t.end_user_tel= #{endUserTel}


    and t.community_id= #{communityId}

order by t.create_time desc

    limit #{page}, #{row}

select p.id, p.apply_order_id applyOrderId, p.res_id resId,p.quantity,p.remark,p.purchase_quantity purchaseQuantity,p.purchase_remark purchaseRemark,p.price purchasePrice,p.original_stock originalStock,p.consult_price consultPrice,p.times_id timesId, r.res_name resName,r.res_code resCode, r.price,r.price standardPrice,rstime.stock,rst.name rstName,rst1.name parentRstName,rss.spec_name specName,rs.supplier_name supplierName, td1.name unitCodeName, td2.name miniUnitCodeName from purchase_apply_detail p inner join resource_store r on p.res_id = r.res_id and r.status_cd = '0' left join t_dict td1 on r.unit_code = td1.status_cd and td1.table_name = 'resource_store' and td1.table_columns = 'unit_code' left join t_dict td2 on r.mini_unit_code = td2.status_cd and td2.table_name = 'resource_store' and td2.table_columns = 'unit_code' LEFT JOIN resource_supplier rs on p.rs_id=rs.rs_id LEFT JOIN resource_store_type rst on r.rst_id=rst.rst_id LEFT JOIN resource_store_type rst1 on r.parent_rst_id=rst1.rst_id LEFT JOIN resource_store_specification rss on r.rss_id=rss.rss_id left join resource_store_times rstime on p.times_id = rstime.times_id and rstime.status_cd = '0' where 1=1 and p.status_cd = '0'
and p.apply_order_id in
#{item}
and t.res_order_type= #{resOrderType}
and p.b_id= #{bId}
and p.operate= #{operate}

