mapper.select:
select t.price,t.apply_order_id,t.apply_order_id applyOrderId,t.status_cd,t.status_cd statusCd,t.store_id,t.store_id storeId,t.stock,t.res_code,t.res_code resCode,t.times_id, t.times_id timesId,t.create_time createTime,(t.price * t.stock) totalPrice, rs.res_name,rs.res_name resName,rs.description,rs.store_id,rs.store_id storeId, rs.stock,rs.warning_stock,rs.warning_stock warningStock,rs.res_id,rs.res_id resId,rs.out_low_price,rs.rss_id,rs.rss_id rssId, rs.out_low_price outLowPrice,rs.out_high_price,rs.out_high_price outHighPrice, rs.unit_code,rs.unit_code unitCode,rs.remark,rs.show_mobile,rs.show_mobile showMobile, rs.sh_id,rs.sh_id shId,sh.sh_name shName,rs.average_price,rs.average_price averagePrice,rss.spec_name rssName,rs.mini_unit_code,rs.mini_unit_code miniUnitCode,rs.mini_stock,rs.mini_stock miniStock,rs.mini_unit_stock,rs.mini_unit_stock miniUnitStock,td.name unitCodeName,td1.name miniUnitCodeName,sh.sh_type shType,rs.parent_rst_id parentRstId,rs.rst_id rstId,rst1.name parentRstName,rst.name rstName, rs.is_fixed,rs.is_fixed isFixed,td2.name isFixedName from resource_store_times t inner join resource_store rs on t.res_code = rs.res_code and rs.status_cd = '0' left join resource_store_type rst on rs.rst_id = rst.rst_id and rst.status_cd = '0' left join resource_store_type rst1 on rs.parent_rst_id = rst1.rst_id and rst1.status_cd = '0' left join resource_store_specification rss on rs.rss_id = rss.rss_id and rss.status_cd = '0' left join storehouse sh on rs.sh_id = sh.sh_id and rs.store_id = sh.store_id and sh.status_cd = '0' left join t_dict td on rs.unit_code = td.status_cd and td.table_name = 'resource_store' and td.table_columns = 'unit_code' left join t_dict td1 on rs.mini_unit_code = td1.status_cd and td1.table_name = 'resource_store' and td1.table_columns = 'unit_code' left join t_dict td2 on rs.is_fixed = td2.status_cd and td2.table_name='resource_store' and td2.table_columns = 'is_fixed' where 1 =1
and rs.res_name like concat('%',#{resName},'%')
and rs.res_name = #{name}
and rs.rst_id= #{rstId}
and rs.parent_rst_id= #{parentRstId}
and t.res_code like concat('%',#{resCode},'%')
and t.price= #{price}
and t.apply_order_id= #{applyOrderId}
and t.status_cd= #{statusCd}
and t.store_id= #{storeId}
and t.stock= #{stock}
and t.stock > 0
and t.res_code in
#{item}
and t.times_id= #{timesId}
and t.sh_id= #{shId}
order by t.create_time desc
limit #{page}, #{row}

mapper.update:
update resource_store_times t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}


    , t.price= #{price}


    , t.apply_order_id= #{applyOrderId}



    , t.stock= #{stock}


    , t.res_code= #{resCode}

where 1=1

    and t.times_id= #{timesId}


    and t.store_id= #{storeId}

mapper.select:
select count(1) count
from resource_store_times t
inner join resource_store rs on t.res_code = rs.res_code and rs.status_cd = '0'
left join resource_store_type rst on rs.rst_id = rst.rst_id and rst.status_cd = '0'
left join resource_store_type rst1 on rs.parent_rst_id = rst1.rst_id and rst1.status_cd = '0'
left join resource_store_specification rss on rs.rss_id = rss.rss_id and rss.status_cd = '0'
left join storehouse sh on rs.sh_id = sh.sh_id and rs.store_id = sh.store_id and sh.status_cd = '0'
left join t_dict td on rs.unit_code = td.status_cd and td.table_name = 'resource_store' and td.table_columns =
'unit_code'
left join t_dict td1 on rs.mini_unit_code = td1.status_cd and td1.table_name = 'resource_store' and
td1.table_columns = 'unit_code'
left join t_dict td2 on rs.is_fixed = td2.status_cd and td2.table_name='resource_store' and td2.table_columns =
'is_fixed'
where 1 =1

    and rs.res_name like concat('%',#{resName},'%')


    and rs.res_name = #{name}


    and rs.rst_id= #{rstId}


    and rs.parent_rst_id= #{parentRstId}


    and t.res_code like concat('%',#{resCode},'%')


    and t.price= #{price}


    and t.apply_order_id= #{applyOrderId}


    and t.status_cd= #{statusCd}


    and t.store_id= #{storeId}


    and t.stock= #{stock}


    and t.stock > 0


    and t.res_code in

        #{item}



    and t.times_id= #{timesId}


    and t.sh_id= #{shId}

select sum(stock) stock
from resource_store_times t
where 1 =1

    and t.price= #{price}


    and t.apply_order_id= #{applyOrderId}


    and t.status_cd= #{statusCd}


    and t.store_id= #{storeId}


    and t.stock= #{stock}


    and t.stock > 0


    and t.res_code= #{resCode}


    and t.res_code in

        #{item}



    and t.times_id= #{timesId}


    and t.sh_id= #{shId}
