mapper.select:
select t.status_cd,t.status_cd statusCd,t.store_user_id,t.store_user_id storeUserId,t.store_id,t.store_id
storeId,t.user_id,t.user_id userId,t.rel_cd,t.rel_cd relCd,uu.tel,uu.name
from s_store_user t
left join u_user uu on t.user_id = uu.user_id
where 1 =1

    and t.status_cd= #{statusCd}


    and t.store_user_id= #{storeUserId}


    and t.store_id= #{storeId}


    and t.user_id= #{userId}


    and t.rel_cd= #{relCd}

order by t.create_time desc

    limit #{page}, #{row}

mapper.update:
update s_store_user t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}


    , t.rel_cd= #{relCd}

where 1=1

    and t.store_user_id= #{storeUserId}


    and t.user_id= #{userId}


    and t.store_id= #{storeId}

mapper.select:
select count(1) count
from s_store_user t
where 1 =1

    and t.status_cd= #{statusCd}


    and t.store_user_id= #{storeUserId}


    and t.store_id= #{storeId}


    and t.user_id= #{userId}


    and t.rel_cd= #{relCd}
