mapper.select:
select t.dps_id,t.dps_id dpsId,t.dp_id,t.dp_id dpId,t.staff_name,t.staff_name
staffName,t.remark,t.status_cd,t.status_cd statusCd,t.community_id,t.community_id
communityId,t.staff_id,t.staff_id staffId,u.tel,u.email,u.address,u.sex
from data_privilege_staff t
left join u_user u on t.staff_id = u.user_id and u.status_cd = '0'
where 1 =1

    and t.dps_id= #{dpsId}


    and t.dp_id= #{dpId}


    and t.staff_name= #{staffName}


    and t.remark= #{remark}


    and t.status_cd= #{statusCd}


    and t.community_id= #{communityId}


    and t.staff_id= #{staffId}

order by t.create_time desc

    limit #{page}, #{row}

mapper.update:
update data_privilege_staff t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}



    , t.dp_id= #{dpId}


    , t.staff_name= #{staffName}


    , t.remark= #{remark}



    , t.staff_id= #{staffId}

where 1=1

    and t.community_id= #{communityId}


    and t.dps_id= #{dpsId}

mapper.select:
select count(1) count
from data_privilege_staff t
where 1 =1

    and t.dps_id= #{dpsId}


    and t.dp_id= #{dpId}


    and t.staff_name= #{staffName}


    and t.remark= #{remark}


    and t.status_cd= #{statusCd}


    and t.community_id= #{communityId}


    and t.staff_id= #{staffId}

select count(1) count from u_user t inner join s_store_user su on t.user_id = su.user_id and su.status_cd = '0' left join data_privilege_staff pu on t.user_id = pu.staff_id and pu.status_cd = '0' and pu.dp_id = #{dpId} where t.status_cd = '0' and pu.dp_id is null
and t.name like concat('%', #{userName},'%')
and su.store_id = #{storeId}
