mapper.select:
select t.store_type,t.store_type storeType,t.ca_id,t.ca_id caId,t.name,t.icon,t.status_cd,t.status_cd
statusCd,t.seq,t.url,t.is_show,t.is_show isShow
from m_menu_catalog t
where 1 =1

    and t.store_type= #{storeType}


    and t.ca_id= #{caId}


    and t.name= #{name}


    and t.icon= #{icon}


    and t.status_cd= #{statusCd}


    and t.seq= #{seq}


    and t.url= #{url}


    and t.is_show= #{isShow}

order by t.seq

    limit #{page}, #{row}

mapper.update:
update m_menu_catalog t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}


    , t.store_type= #{storeType}


    , t.name= #{name}


    , t.icon= #{icon}


    , t.seq= #{seq}


    , t.url= #{url}


    , t.is_show= #{isShow}

where 1=1

    and t.ca_id= #{caId}

mapper.select:
select count(1) count
from m_menu_catalog t
where 1 =1

    and t.store_type= #{storeType}


    and t.ca_id= #{caId}


    and t.name= #{name}


    and t.icon= #{icon}


    and t.status_cd= #{statusCd}


    and t.seq= #{seq}


    and t.url= #{url}


    and t.is_show= #{isShow}

SELECT mm.`m_id` `mId`,mm.name menuName,mm.`g_id` gId,mm.`url`,mm.seq menuSeq,mm.`p_id` pId, mm.`description` menuDescription, mmg.name menuGroupName,mmg.`icon`,mmg.`label`,mmg.`seq` menuGroupSeq, mmg.`description` menuGroupDescription,mm.is_show isShow,mm.description FROM m_menu mm inner join m_menu_group mmg on mm.`g_id` = mmg.`g_id` AND mmg.`status_cd` = '0'
inner join m_menu_group_catalog mgc on mmg.g_id = mgc.g_id and mgc.status_cd = '0'
inner join m_menu_group_community mgcy on mgcy.g_id = mmg.g_id and mgcy.status_cd = '0'
WHERE mmg.group_type = #{groupType} and mmg.store_type= #{domain}
and mgc.ca_id = #{caId}
and mgcy.community_id = #{communityId}
AND mm.`status_cd` = '0' AND mm.`m_id` IN ( SELECT pp.`m_id` FROM p_privilege_user ppu,p_privilege pp WHERE ppu.`p_id` = pp.`p_id` and pp.domain in (#{domain},'9999') AND ppu.`privilege_flag` = '0' AND ppu.`user_id` = #{userId} AND ppu.`status_cd` = '0' AND pp.`status_cd` = '0' UNION SELECT pp.`m_id` FROM p_privilege_user ppu,p_privilege_group ppg,p_privilege pp,p_privilege_rel ppr WHERE ppu.`p_id` = ppr.pg_id AND ppr.pg_id = ppg.pg_id AND ppr.p_id = pp.`p_id` and pp.domain in (#{domain},'9999') AND ppu.`privilege_flag` = '1' AND ppu.`user_id` = #{userId} AND ppu.`status_cd` = '0' AND pp.`status_cd` = '0' AND ppg.status_cd = '0' AND ppr.status_cd = '0' )
