mapper.select:
select t.rel_id,t.rel_id relId,t.remark,t.status_cd,t.status_cd statusCd,t.start_time,t.start_time
startTime,t.state,t.end_time,t.end_time endTime,t.owner_id,t.owner_id ownerId,t.user_id,t.user_id
userId,t.room_id,t.room_id roomId,bo.name ownerName,bo.link
from building_owner_room_rel t
left join building_owner bo on t.owner_id = bo.member_id and bo.status_cd = '0'
where 1 =1

    and bo.`name` like concat('%',#{ownerNameLike},'%')


    and t.rel_id= #{relId}


    and t.remark= #{remark}


    and t.status_cd= #{statusCd}


    and t.start_time= #{startTime}


    and t.state= #{state}


    and t.end_time= #{endTime}


    and t.owner_id= #{ownerId}


    and t.user_id= #{userId}


    and t.room_id= #{roomId}

order by t.create_time desc

    limit #{page}, #{row}

mapper.update:
update building_owner_room_rel t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}


    , t.remark= #{remark}


    , t.start_time= #{startTime}


    , t.state= #{state}


    , t.end_time= #{endTime}


    , t.owner_id= #{ownerId}


    , t.user_id= #{userId}


    , t.room_id= #{roomId}

where 1=1

    and t.rel_id= #{relId}

mapper.select:
select count(1) count
from building_owner_room_rel t
where 1 =1

    and t.rel_id= #{relId}


    and t.remark= #{remark}


    and t.status_cd= #{statusCd}


    and t.start_time= #{startTime}


    and t.state= #{state}


    and t.end_time= #{endTime}


    and t.owner_id= #{ownerId}


    and t.user_id= #{userId}


    and t.room_id= #{roomId}

select t.owner_id ownerId,count(1) roomCount from building_owner_room_rel t left join building_room br on br.room_id = t.room_id where t.status_cd = '0' and br.status_cd = '0' and t.owner_id in
#{item}
and br.state != '2008' group by t.owner_id
