SELECT t.sex,t.name,t.link,t.status_cd,t.status_cd statusCd,t.remark,t.owner_id,t.owner_id ownerId,t.b_id,t.b_id bId,t.user_id,t.user_id userId,t.age,t.member_id,t.member_id memberId,t.owner_type_cd,t.owner_type_cd ownerTypeCd, r.`room_id` roomId,r.`room_num` roomNum,t.community_id,t.community_id communityId,t.id_card, t.id_card idCard,orr.start_time startTime ,orr.end_time endTime,t.address FROM building_room r,building_owner_room_rel orr,building_owner t WHERE r.`room_id` = orr.`room_id` AND orr.`owner_id` = t.`owner_id`
AND r.`room_id` = #{roomId}
and r.room_id in
#{item}
and t.community_id= #{communityId}
and t.owner_type_cd = '1001' AND r.`status_cd` = '0' AND orr.`status_cd` = '0' AND t.`status_cd` = '0'

select ps.num,ps.ps_id psId,t.sex,t.name,t.link,t.status_cd,t.status_cd statusCd,t.remark,t.owner_id,t.owner_id ownerId,t.b_id,t.b_id bId,t.address, t.user_id,t.user_id userId,t.age,t.member_id,t.member_id memberId, t.owner_type_cd,t.owner_type_cd ownerTypeCd,t.id_card, t.id_card idCard from p_parking_space ps , owner_car oc,building_owner t where ps.ps_id = oc.ps_id and oc.owner_id = t.owner_id AND oc.`status_cd` = '0' AND ps.`status_cd` = '0' and oc.car_type_cd='1001' AND t.`status_cd` = '0'
and ps.ps_id in
#{item}
AND ps.`ps_id` = #{psId}

select COUNT(1) count
from building_owner_room_rel t
    inner join building_owner bo
on t.owner_id = bo.owner_id and bo.status_cd = '0' and bo.community_id = #{communityId}
where bo.owner_type_cd = '1001'
  and t.room_id = #{roomId}
