mapper.select:
select t.id_card,t.id_card idCard,t.sex,t.link,t.owner_type_cd,t.owner_type_cd
ownerTypeCd,t.remark,t.status_cd,t.status_cd statusCd,t.owner_id,t.owner_id ownerId,t.user_id,t.user_id
userId,t.name,t.state,t.community_id,t.community_id communityId,t.age,t.member_id,t.member_id memberId,
t.address from building_owner t
where 1 =1

    and t.id_card= #{idCard}


    and t.sex= #{sex}


    and t.link= #{link}


    and t.address= #{address}


    and t.owner_type_cd= #{ownerTypeCd}


    and t.owner_type_cd in

        #{item}



    and t.remark= #{remark}


    and t.status_cd= #{statusCd}


    and t.owner_id= #{ownerId}


    and t.owner_id in

        #{item}



    and t.user_id= #{userId}


    and t.name= #{name}


    and t.name like concat('%',#{nameLike},'%')


    and t.state= #{state}


    and t.community_id= #{communityId}


    and t.age= #{age}


    and t.member_id= #{memberId}

order by t.create_time desc

    limit #{page}, #{row}

mapper.update:
update building_owner t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}

,t.id_card= #{idCard}

    ,t.sex= #{sex}


    ,t.link= #{link}


    ,t.address= #{address}


    ,t.owner_type_cd= #{ownerTypeCd}


    ,t.remark= #{remark}


    ,t.owner_id= #{ownerId}


    ,t.user_id= #{userId}


    ,t.name= #{name}


    ,t.state= #{state}


    ,t.community_id= #{communityId}


    ,t.age= #{age}

where 1=1

    and t.member_id= #{memberId}

mapper.select:
select count(1) count
from building_owner t
where 1 =1

    and t.id_card= #{idCard}


    and t.sex= #{sex}


    and t.link= #{link}


    and t.address= #{address}


    and t.owner_type_cd= #{ownerTypeCd}


    and t.owner_type_cd in

        #{item}



    and t.remark= #{remark}


    and t.status_cd= #{statusCd}


    and t.owner_id= #{ownerId}


    and t.owner_id in

        #{item}



    and t.user_id= #{userId}


    and t.name= #{name}


    and t.name like concat('%',#{nameLike},'%')


    and t.state= #{state}


    and t.community_id= #{communityId}


    and t.age= #{age}


    and t.member_id= #{memberId}

select t.owner_id ownerId,count(1) memberCount from building_owner t where t.status_cd = '0' and t.owner_type_cd != '1001' and t.owner_id in
#{item}
group by t.owner_id
