mapper.select:
select t.privilege_flag,t.privilege_flag privilegeFlag,t.pu_id,t.pu_id puId,t.p_id,t.p_id
pId,t.status_cd,t.status_cd statusCd,t.store_id,t.store_id storeId,t.user_id,t.user_id userId

    ,pg.`name` roleName

from p_privilege_user t

    left join p_privilege_group pg on t.p_id = pg.pg_id and pg.status_cd = '0'

where 1 =1

    and t.privilege_flag= #{privilegeFlag}


    and t.pu_id= #{puId}


    and t.p_id= #{pId}


    and t.status_cd= #{statusCd}


    and t.store_id= #{storeId}


    and t.user_id= #{userId}

order by t.create_time desc

    limit #{page}, #{row}

mapper.update:
update p_privilege_user t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}


    , t.privilege_flag= #{privilegeFlag}


    , t.p_id= #{pId}


    , t.store_id= #{storeId}


    , t.user_id= #{userId}

where 1=1

    and t.pu_id= #{puId}

mapper.select:
select count(1) count
from p_privilege_user t

    left join p_privilege_group pg on t.p_id = pg.pg_id and pg.status_cd = '0'

where 1 =1

    and t.privilege_flag= #{privilegeFlag}


    and t.pu_id= #{puId}


    and t.p_id= #{pId}


    and t.status_cd= #{statusCd}


    and t.store_id= #{storeId}


    and t.user_id= #{userId}

select count(1) count
from p_privilege_user t
inner JOIN u_user uu on t.user_id = uu.user_id and uu.status_cd = '0'
where 1 =1

    and t.privilege_flag= #{privilegeFlag}


    and t.pu_id= #{puId}


    and t.p_id= #{pId}


    and t.status_cd= #{statusCd}



    and t.store_id= #{storeId}


    and t.user_id= #{userId}


    and uu.name like concat('%', #{userName},'%')

select t.privilege_flag,t.privilege_flag privilegeFlag,t.pu_id,t.pu_id puId,t.p_id,t.p_id
pId,t.status_cd,t.status_cd statusCd,t.store_id,t.store_id storeId,t.user_id,t.user_id userId,
uu.address,uu.sex,uu.user_id,uu.user_id userId,uu.level_cd,uu.level_cd
levelCd,uu.location_cd,uu.location_cd locationCd,uu.score,uu.name,uu.tel,uu.email,uu.age
from p_privilege_user t
inner JOIN u_user uu on t.user_id = uu.user_id and uu.status_cd = '0'
where 1 =1

    and t.privilege_flag= #{privilegeFlag}


    and t.pu_id= #{puId}


    and t.p_id= #{pId}


    and t.status_cd= #{statusCd}


    and t.store_id= #{storeId}


    and t.user_id= #{userId}


    and uu.name like concat('%', #{userName},'%')

order by t.create_time desc

    limit #{page}, #{row}

select count(*) count from (
select count(1) count from u_user t inner join s_store_user su on t.user_id = su.user_id and su.status_cd = '0' left join p_privilege_user pu on t.user_id = pu.user_id and pu.status_cd = '0' and pu.privilege_flag = '1' and pu.p_id = #{pId}
LEFT JOIN u_org_staff_rel uosr on t.user_id=uosr.staff_id
where t.status_cd = '0' and pu.pu_id is null
and t.name like concat('%', #{userName},'%')
and su.store_id = #{storeId}
and uosr.org_id in
#{item}
AND uosr.status_cd = '0' group by t.user_id
)a

select t.user_id,t.user_id userId, t.address,t.sex,t.user_id,t.user_id userId,t.level_cd,t.level_cd levelCd,t.location_cd,t.location_cd locationCd,t.score,t.name,t.tel,t.email,t.age from u_user t inner join s_store_user su on t.user_id = su.user_id and su.status_cd = '0' left join p_privilege_user pu on t.user_id = pu.user_id and pu.status_cd = '0' and pu.privilege_flag = '1' and pu.p_id = #{pId}
LEFT JOIN u_org_staff_rel uosr on t.user_id=uosr.staff_id
where t.status_cd = '0' and pu.pu_id is null
and t.name like concat('%', #{userName},'%')
and su.store_id = #{storeId}
and uosr.org_id in
#{item}
AND uosr.status_cd = '0' group by t.user_id
order by t.create_time desc
limit #{page}, #{row}
