mapper.select:
select t.address,t.sex,t.status_cd,t.status_cd statusCd,t.user_id,t.user_id userId,t.level_cd,t.level_cd
levelCd,t.location_cd,t.location_cd locationCd,t.score,t.password,t.name,t.tel,t.b_id,t.b_id bId,t.email,t.age
from u_user t
where 1 =1

    and t.address= #{address}


    and t.sex= #{sex}


    and t.status_cd= #{statusCd}


    and t.user_id= #{userId}


    and t.level_cd= #{levelCd}


    and t.location_cd= #{locationCd}


    and t.score= #{score}


    and t.password= #{password}


    and t.name= #{name}


    and t.tel= #{tel}


    and t.b_id= #{bId}


    and t.email= #{email}


    and t.age= #{age}

order by t.create_time desc

    limit #{page}, #{row}

mapper.update:
update u_user t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}


    , t.address= #{address}


    , t.sex= #{sex}


    , t.level_cd= #{levelCd}


    , t.location_cd= #{locationCd}


    , t.score= #{score}


    , t.password= #{password}


    , t.name= #{name}


    , t.tel= #{tel}

, t.email= #{email}

    , t.age= #{age}

where 1=1

    and t.user_id= #{userId}


    and t.b_id= #{bId}

mapper.select:
select count(1) count
from u_user t
where 1 =1

    and t.address= #{address}


    and t.sex= #{sex}


    and t.status_cd= #{statusCd}


    and t.user_id= #{userId}


    and t.level_cd= #{levelCd}


    and t.location_cd= #{locationCd}


    and t.score= #{score}


    and t.password= #{password}


    and t.name= #{name}


    and t.tel= #{tel}


    and t.b_id= #{bId}


    and t.email= #{email}


    and t.age= #{age}

select count(DISTINCT t.user_id) count
from u_user t
inner join s_store_user su on t.user_id = su.user_id and su.status_cd = '0'
LEFT JOIN u_org_staff_rel osr on su.user_id = osr.staff_id and osr.status_cd = '0'  and osr.org_id = #{orgId}
where t.level_cd = '01'
and su.store_id = #{storeId}
and  osr.org_id is null

    and t.name like concat('%', #{staffName},'%')


    and osr.org_id in

        #{item}

select DISTINCT t.address,t.sex,t.status_cd,t.status_cd statusCd,t.user_id,t.user_id userId,t.level_cd,t.level_cd levelCd,t.location_cd,t.location_cd locationCd,t.score,t.name,t.tel,t.b_id,t.b_id bId,t.email,t.age,t.create_time from u_user t inner join s_store_user su on t.user_id = su.user_id and su.status_cd = '0' LEFT JOIN u_org_staff_rel osr on su.user_id = osr.staff_id and osr.status_cd = '0' and osr.org_id = #{orgId} where t.level_cd = '01' and su.store_id = #{storeId} and osr.org_id is null
and t.name like concat('%', #{staffName},'%')
and osr.org_id in
#{item}
order by t.create_time desc
limit #{page}, #{row}
